//SPDX-License-Identifier: CC0-1.0
pragma solidity ^0.8.10;
import './IERC20.sol';

abstract contract ERC20Basic is IERC20{

    //代币名称（Ethereum）
    string private _name;

    //代币符号(比如ETH)
    string private _symbol;

    //代币精度(表示代币的最小单位，通用为18，分割到小数点后面18位)
    uint8 private _decimals;

    //代币的总供应量
    uint256   private _totalSupply;

    //存储每个地址的代币余额
    mapping(address => uint256) private _balances;

    //存储授权信息，记录每个地址授权给其它地址的代币数量
    mapping (address => mapping(address => uint256)) private _allowances; 

    constructor(string memory name_, string memory symbol_){
        _name = name_;
        _symbol = symbol_;
        _decimals = 18;
    }

    function name() public view returns (string memory){
        return _name;
    }

    function symbol() public view returns (string memory){
        return _symbol;
    }

    function toralSupply() public view returns (uint256){
        return _totalSupply;
    }

    function balanceOf(address account) external view override returns (uint256){
        return _balances[account];
    }
}

contracts/IERC20.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.10;

interface IERC20 {
    //当代币从一个账户转移到另外一个账户
    event Transfer(address indexed from ,address indexed to ,uint256 value);
    //当一个账户授权另一个账户使用其代币时触发
    event Approval(address indexed owner, address indexed spender, uint256 value);

    //返回代币的总供应量
    function totalSupply() external view returns (uint256);

    //返回指定账户的代币余额
    function balanceOf(address account) external view returns (uint256);

    //向指定地址转账代币
    function trasfer(address account, uint256 value) external returns (uint256);

    //返回授权额度
    function allowance(address owner, address spender) external view returns (uint256);
    
    //授权指定地址
    function approve(address owner, uint256 value) external returns (bool);

    //通过授权转账代币
    function transferFrom(address from, address to, uint256 value) external returns (bool);
}
